  `autokeyword` varchar(30) NOT NULL DEFAULT '0' COMMENT '自动关键词来源',
  `autothumb` varchar(30) NOT NULL DEFAULT '0' COMMENT '自动缩略图来源',
  `click_defmin` int(11) NOT NULL DEFAULT '0' COMMENT '默认点击数(最小值)',
  `click_defmax` int(11) NOT NULL DEFAULT '0' COMMENT '默认点击数(最大值)',
