  `ccid14` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '地铁站点',
  `ccid17` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '新房价格区间',
  `ccid31` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '商品分类',
  `ccid33` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '装修费用区间',
  `ccid32` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '设计风格',
  `ccid35` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '问题状态',
  `ccid36` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '高额悬赏区间',
  `ccid38` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '精华',
