  `ccid43` smallint(6) unsigned NOT NULL DEFAULT '0',
  `ccid45` smallint(6) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`cnid`),
  UNIQUE KEY `enamesid` (`ename`)
) ENGINE=MyISAM  DEFAULT CHARSET=gbk COMMENT='类目节点表' AUTO_INCREMENT=1073 ;

