-- 表的结构 `cms_dbdebugs`
--

CREATE TABLE IF NOT EXISTS `cms_dbdebugs` (
  `ddid` varchar(32) NOT NULL COMMENT 'id',
  `ddsql` text NOT NULL COMMENT 'sql语句',
  `ddtbl` varchar(255) NOT NULL COMMENT '数据表',
  `ddused` float unsigned NOT NULL DEFAULT '0' COMMENT '时间(毫秒)',
  `ddurl` varchar(255) NOT NULL COMMENT '所在url',
  `ddtpl` varchar(255) NOT NULL COMMENT '所在摸版',
  `ddtag` varchar(255) NOT NULL COMMENT '所在标签',
  `ddate` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发生时间',
  PRIMARY KEY (`ddid`)
) ENGINE=MyISAM DEFAULT CHARSET=gbk COMMENT='SQL诊断分析';

