CREATE TABLE IF NOT EXISTS `cms_dbsources` (
  `dsid` smallint(6) unsigned NOT NULL AUTO_INCREMENT COMMENT '外部数据源编号',
  `cname` varchar(50) NOT NULL COMMENT '外部数据源名称',
  `dbhost` varchar(30) NOT NULL COMMENT '数据库服务器',
  `dbuser` varchar(50) NOT NULL COMMENT '数据库用户',
  `dbpw` varchar(100) NOT NULL COMMENT '数据库密码 	',
  `dbname` varchar(50) NOT NULL COMMENT '数据库名称',
  `dbcharset` varchar(10) NOT NULL COMMENT '数据库字符集',
  PRIMARY KEY (`dsid`)
) ENGINE=MyISAM DEFAULT CHARSET=gbk COMMENT='外部数据源表' AUTO_INCREMENT=1 ;

